mapper.select:
SELECT member_no, project_no, member_position
FROM project_member
WHERE project_no=#{project_no} and member_no=#{member_no}

mapper.update:
UPDATE project_member
SET member_position = 1
WHERE project_no = #{project_no}

UPDATE project_member
SET member_position = 0
WHERE project_no=#{project_no} and member_no=#{member_no}

mapper.select:
SELECT COUNT(*)
FROM project_member
WHERE project_no = #{project_no}
